import React, { Component } from "react";
import "./App.css";
import Header from './Components/Header/Index.js';
import Main from "./Components/Main/Index.js";
import Footer from './Components/Footer/Index.js';
import { fetchCharacters } from "./Services/ApiBook";
import GenerList from './Components/Genrelist/Index';

const hello = "hello";

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      books: fetchCharacters,
      enerList:[] 
    };

  }
  componentDidMount() {
    const dataWithId = fetchCharacters.map(function(item, i) {
      return {
        ...item,
        id: i + 1
      };

    });
    this.setState({
      books: dataWithId
    });

}
  render() {
    const { books } = this.state;

    return (
      <div className="App">
        <Header greeting={hello} />
        <Main books={books}/>
        <Footer/>
      </div>
    );
  }



}

export default App;
