import React, { Component } from "react";
import "./App.css";
import Header from './Components/Header/Index.js';
import Main from "./Components/Main/Index.js";
import Footer from './Components/Footer/Index.js';
import { fetchCharacters } from "./Services/ApiBook";

const hello = "hello";

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      books: fetchCharacters,
      genreList: []
    };

  }
  componentDidMount() {
    const dataWithId = fetchCharacters.map(function(item, i) {
      return {
        ...item,
        id: i + 1
      };

    });
    this.setState({
      books: dataWithId
    });
    this.recopileGenres(dataWithId);

}
  recopileGenres(books){
    const auxArray = [];

    books.map(function(item, i) {
      item.genres.map(function(item, i){
        if (!auxArray.indexOf(item)){
          auxArray.push(item);
        }
      });

    });
    console.log(auxArray);

  }
  render() {
    const { books } = this.state;

    return (
      <div className="App">
        <Header greeting={hello} />
        <Main books={books}/>
        <Footer/>
      </div>
    );
  }



}

export default App;
