			const newState = {
				books: prevState.books.map((book, ind) => {

          if (ind === idBook) {

							book = {
								...book,
								tittle: "pepe"
              }
              console.log(book);
						}

					return book;
				})
			}
			return newState;
		})
  }
  componentDidMount() {
    const auxArray = [];
    booksJson.map(function (item, i) {
      item.genres.map(function (item, i) {
        if (auxArray.indexOf(item) === -1) {
          auxArray.push(item);
        }
      });
    })
    this.setState({
      genreList: auxArray
    });

  }

  render() {
    const { books } = this.state;
    return (
      <div className="App">
        <Header />
        <Main books={books} deleteBook={this.deleteBook} />
      </div>
    );
  }



}

export default App;
