import React, { Component } from "react";
import { Link } from "react-router-dom";

import "./Bookedit.css";

class BookEdit extends Component {
  componentDidMount () {

  }

  render() {
    const { match, books } = this.props;
    const selectedId = match.params.id;
    const selectedCharacter = books.find(
      character => character.id === selectedId
    );
    return (


      <form className="app__form">
        <label htmlFor="character-name" className="character-name__label">
          Nombre
          <input
            type="text"
            name="character-name"
            id="character-name"
            value=""
            className="character-name__input"
            placeholder={selectedCharacter.name}
          />
        </label>
        <label htmlFor="character-name" className="character-name__label">
          descripcion
          <input
            type="text"
            name="character-name"
            id="character-name"
            value=""
            className="character-name__input"
            placeholder={selectedCharacter.detail}
          />
        </label>
        <label htmlFor="character-name" className="character-name__label">
          genre

        </label>
        <div className="app__character-detail col-md-10">
        <Link
        className="app__character-link"
        to={`/`}
      >
       <p>pa tras wey</p>
        </Link>
      </div>
      </form>

    );
  }
}


export default BookEdit;
