import React, { Component } from "react";


class ModalEditBook extends Component {
  state = {
    open: false,
  };
  	constructor(props) {
		super(props);

		this.onBookUpdate = this.onBookUpdate.bind(this);
	}


	onBookUpdate(e) {
    console.log(e.currentTarget);
    const { bookSelected, handleChangeEdit } = this.props;
		handleChangeEdit(bookSelected);
	}

  handleOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
};
  render() {
    const { bookSelected} = this.props;
    return (
            <div>
                <input type="text" inputName="tittle" value={bookSelected.tittle} onChange={this.onBookUpdate} />
                <input type="text" inputName="price" value={bookSelected.price} onChange={this.onBookUpdate} />
                <input type="text" inputName="isbn" value={bookSelected.isbn} onChange={this.onBookUpdate} />
                <textarea type="text" inputName="resume" value={bookSelected.resume}  onChange={this.onBookUpdate} />
            </div>

    );
  }
}


export default ModalEditBook;
