    //             }
    //           })
    //           return array;
		// 		})
		// 	}
		// 	return newState;
    // })
    // console.log(this.state);
     const idBook = e.target.id;
    console.log(idBook);
    this.setState(prevState => {
			const newState = {
				books: prevState.books.filter((ind) => {
					return ind !== idBook;
				})
			}
			return newState;
    })
    console.log(this.state.books);
  }
  componentDidMount() {
    const auxArray = [];
    booksJson.map(function (item, i) {
      item.genres.map(function (item, i) {
        if (auxArray.indexOf(item) === -1) {
          auxArray.push(item);
        }
      });
    })
    this.setState({
      genreList: auxArray
    });

  }

  render() {
    const { books } = this.state;
    return (
      <div className="App">
        <Header />
        <Main 
            books={books}
            deleteBook={this.deleteBook}/>
      </div>
    );
  }



}

export default App;
