import React, { Component } from "react";
import "./App.css";
import Header from './Components/Header/Index.js';
import Main from "./Components/Main/Index.js";
import { booksJson } from "./Services/ApiBook";


class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      books: booksJson,
      genreList: []
    };
    this.deleteBook = this.deleteBook.bind(this);

  }
  deleteBook(e){
    var array = [...this.state.books]; // make a separate copy of the array
    // array.map(function (item, i) {
    //   if (item.id == e.target.id){
    //     console.log("borrado");
    //      array.splice(i, 1);
    //   }
    // })
    // this.setState({
    //   books: array
    // });
    // console.log(this.state.books);


    const idBook = e.target.id;
    console.log(idBook);
    this.setState(prevState => {
			const newState = {
				books: prevState.books.filter((book, index) => {
					    array.map(function (item, i) {
                if (item.id == idBook){
                  console.log("borrado");
                   array.splice(i, 1);
                }
              })
              return array;
				})
			}
			return newState;
    })
    console.log(this.state );
  }
  componentDidMount() {
    const auxArray = [];
    booksJson.map(function (item, i) {
      item.genres.map(function (item, i) {
        if (auxArray.indexOf(item) === -1) {
          auxArray.push(item);
        }
      });
    })
    this.setState({
      genreList: auxArray
    });

  }

  render() {
    const { books } = this.state;
    return (
      <div className="App">
        <Header />
        <Main 
            books={books}
            deleteBook={this.deleteBook}/>
      </div>
    );
  }



}

export default App;
