import React, { Component } from "react";
import { Link } from "react-router-dom";

import "./Bookedit.css";

class BookEdit extends Component {
  componentDidMount () {

  }
  	onAddGenre() {
		const { bookInd, handleAddGenre } = this.props;
		handleAddGenre(bookInd);
	}
  ondeleteBook(){
    const { deleteButton } = this.props;
    const { selectedId } = this.props.match.params.id;
    deleteButton(selectedId);
  }
  render() {
    const { match, books } = this.props;
    const selectedId = match.params.id;
    const selectBook = books.find(
      book => book.id === selectedId
    );

    return (
    <div className="col-md-10">
      <div className="row">
        <div className="col-md-4 tittleBook">{selectBook.tittle}</div>
      </div>
      <div className="row">
        <div className="col-md-12">{selectBook.resume}</div>
      </div>
      <div className="row">
        {selectBook.genres.map(item => (
          <React.Fragment key={item}>
            <div className="col-md-1 genreList">{item}</div>
          </React.Fragment>
        ))}     
       </div>
        <div className="app__character-detail col-md-10">
        <Link
        className="app__character-link"
        to={`/`}
      >
       <p>pa tras wey</p>
        </Link>
      </div>
        <button onClick={this.props.deleteBook}> booooom </button>  
      </div>
    );
  }
}


export default BookEdit;
