import React, { Component } from "react";
import "./App.css";
import Header from './Components/Header/Index.js';
import Main from "./Components/Main/Index.js";
import Footer from './Components/Footer/Index.js';
import { fetchCharacters } from "./Services/ApiBook";

const hello = "hello";

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      books: [],
      book: []
    };

    this.handleInputChange = this.handleInputChange.bind(this);
  }

  handleInputChange(event) {
    const currentQuery = event.currentTarget.value;
    this.setState({ books: currentQuery });
  }
  render() {
    const { books } = this.state;

    return (
      <div className="App">
        <Header greeting={hello} />
        <Main books={books}/>
        <Footer/>
      </div>
    );
  }


  componentDidMount() {
        const dataWithId = fetchCharacters.map(function(item, i) {
          return {
            ...item,
            id: i
          };
        });

        this.setState({
          books: dataWithId
        });

  }
}

export default App;
