import React, { Component } from "react";
import "./App.css";
import Header from './Components/Header/Index.js';
import Main from "./Components/Main/Index.js";
import Footer from './Components/Footer/Index.js';
import { fetchCharacters } from "./Services/ApiBook";

const hello = "hello";

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      books: fetchCharacters,
      genreList: []
    };

  }
  componentDidMount() {
    const auxArray = [];
    const dataWithId = fetchCharacters.map(function(item, i) {
      item.genres.map(function(item, i){
        if (auxArray.indexOf(item) === -1){
          auxArray.push(item);
        }
    });
    this.setState({
      genreList: auxArray
    });

  })
}

  render() {
    const { books } = this.state;
    const { genreList } = this.state;
    console.log(genreList);
    return (
      <div className="App">
        <Header greeting={hello} />
        <Main books={books} genreList={genreList}/>
        <Footer/>
      </div>
    );
  }



}

export default App;
