import React, { Component } from "react";


class ModalEditBook extends Component {
  state = {
    open: false,
  };
	onBookUpdate(e) {
		const { value, name } = e.currentTarget;
		const { bookSelected } = this.props;
		handleChangeEdit(bookSelected);
	}

  handleOpen = () => {
    this.setState({ open: true });
  };

  handleClose = () => {
    this.setState({ open: false });
};
  render() {
    const { bookSelected} = this.props;
    return (
            <div>
                <input type="text" value={bookSelected.tittle} handleInputChange={this.onBookUpdate} />
                <input type="text" value={bookSelected.price} handleInputChange={this.onBookUpdate} />
                <input type="text" value={bookSelected.isbn} handleInputChange={this.onBookUpdate} />
                <textarea type="text" value={bookSelected.resume}  handleInputChange={this.onBookUpdate} />
            </div>

    );
  }
}


export default ModalEditBook;
